			["authorOptions"] = {
			},
			["adjustedMax"] = "100%",
			["customText"] = "function()\n    if aura_env.state and aura_env.state.unitId then\n        SetPortraitTexture(aura_env.region.icon, aura_env.state.unitId)\n    end\n    return\nend",
			["yOffset"] = 0,
			["anchorPoint"] = "CENTER",
			["sparkRotation"] = 0,
			["sparkRotationMode"] = "AUTO",
			["url"] = "https://wago.io/YpNgieG5Y/10",
			["icon"] = true,
			["triggers"] = {
				{
					["trigger"] = {
						["type"] = "custom",
						["subeventSuffix"] = "_CAST_START",
						["customVariables"] = "{\n    threatPct = {\n        display = \"Threat Percent\",\n        type = \"number\",\n    }, \n    absThreatDiff = {\n        display = \"Absolute Threat Diff\",\n        type = \"number\",\n    },\n    threatToAggro = {\n        display = \"Threat until Aggro\",\n        type = \"number\",\n    }, \n    unitId = {\n        display = \"Unit ID\",\n        type = \"number\",\n    },\n    unitTargeted = {\n        display = \"Targeted Unit\",\n        type = \"bool\",\n    },\n    status = {\n        display = \"Threat Status\",\n        type = \"select\",\n        values = {\n            [1] = \"Untanked\", \n            [2] = \"Insecurely Tanking\", \n            [3] = \"Tanking\",\n        }\n    },\n}",
						["event"] = "Health",
						["unit"] = "player",
						["subeventPrefix"] = "SPELL",
						["spellIds"] = {
						},
						["events"] = "UNIT_THREAT_LIST_UPDATE, CLEU:UNIT_DIED, PLAYER_REGEN_ENABLED, PLAYER_TARGET_CHANGED",
						["names"] = {
						},
						["check"] = "event",
						["custom"] = "function(allStates, event, ...)\n    if event == \"UNIT_THREAT_LIST_UPDATE\" then\n        local unitId = ...\n        if UnitIsPlayer(unitId) or not unitId then\n            return\n        end\n        local isTanking, threatStatus, scaledPercentage, _, threatValue = UnitDetailedThreatSituation(\"player\", unitId)\n        if not threatValue then\n            return \n        end\n        \n        local _, firstThreat, _, secondThreat = aura_env.ThreatFunc(unitId)\n        local guid = UnitGUID(unitId)\n        allStates[guid] = allStates[guid] or {unit = unitId}\n        local state = allStates[guid]\n        \n        local threatDiff\n        local threatToAggro\n        if isTanking then\n            threatDiff = threatValue - secondThreat\n            threatToAggro = threatDiff\n        else\n            threatDiff = threatValue - firstThreat\n            if scaledPercentage ~= 0 then\n                threatToAggro = (threatValue / (scaledPercentage/100)) - threatValue\n            else\n                threatToAggro = threatDiff\n            end\n        end\n        \n        state.changed = true\n        state.show = true\n        state.progressType = \"static\"\n        state.total = 100\n        state.value = Round(scaledPercentage * 10) / 10\n        local raidMarker = GetRaidTargetIndex(unitId)\n        if raidMarker then\n            state.name = aura_env.getIconTexture(raidMarker) .. UnitName(unitId)\n        else\n            state.name = UnitName(unitId)\n        end\n        \n        state.unitId = unitId\n        state.threatPct = state.value\n        state.unitTarget = unitId .. \"target\"\n        state.absThreatDiff = AbbreviateNumbers(Round(math.abs(threatDiff) / 100)) or threatDiff\n        state.threatToAggro = AbbreviateNumbers(Round(threatToAggro / 100)) or threatToAggro\n        state.status = threatStatus < 1 and 1 or threatStatus\n        state.unitTargeted = UnitGUID(\"target\") == guid\n        \n        -- if the mob starts casting a heal on another mob, this will stop the tank's name and threat from disappearing\n        local tankThreat = select(5, UnitDetailedThreatSituation(unitId .. 'target', unitId)) or 0\n        if not state.tankThreat or tankThreat > 0 then\n            state.tankThreat = tankThreat\n            state.threatToAggro = AbbreviateNumbers(Round(threatToAggro / 100)) or threatToAggro\n            state.unitTarget = UnitName(unitId .. 'target')\n        end\n        \n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" and select(8, ...) ~= aura_env.playerGUID then  -- Unit Died\n        local guid = select(8, ...)\n        if allStates[guid] then\n            allStates[guid].show = false\n            allStates[guid].changed = true\n        end\n        \n    elseif event == \"PLAYER_REGEN_ENABLED\" or (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and select(8, ...) == aura_env.playerGUID) then  -- Combat Ended or Player Died\n        for _, v in pairs(allStates) do\n            v.show = false\n            v.changed = true\n        end\n    end\n    \n    -- Current target changed\n    if event == \"PLAYER_TARGET_CHANGED\" then\n        for guid, state in pairs(allStates) do\n            state.unitTargeted = UnitGUID(\"target\") == guid\n            state.changed = true\n        end\n        return true\n    end\n    return true\nend",
