		["XP Session / XP Per Hour"] = {
			["outline"] = "OUTLINE",
			["wagoID"] = "QYsJ2-8cl",
			["xOffset"] = -512.5809631347656,
			["displayText"] = "%c",
			["customText"] = "function()\n    local xp_per_h, ding_t, t = aura_env.get_data()\n    -- Ding time can be infinite, so only format if time is valid\n    ding_t = ding_t == 1/0 and \"...\" or aura_env.sec_time_str(math.floor(ding_t))\n    \n    return string.format(\n        \"|cffb76b45XP/hr|r: %sk\\\n        |cffffff00Ding:|r %s\\\n        |cffffff00Time:|r %s\"\n        , floor(xp_per_h / 100) / 10\n        , ding_t\n        , aura_env.sec_time_str(t)\n    )\nend    \n\n",
			["yOffset"] = 35.55520629882813,
			["anchorPoint"] = "CENTER",
			["customTextUpdate"] = "update",
			["url"] = "https://wago.io/QYsJ2-8cl/5",
			["actions"] = {
				["start"] = {
				},
				["finish"] = {
				},
				["init"] = {
					["custom"] = "\nlocal xp_max = UnitXPMax(\"player\") -- Amount of XP for this level\nlocal xp_carry = 0 -- XP carried from previous levels this session\nlocal xp_start = UnitXP(\"player\") -- Amount of current xp\nlocal ts = time() -- Session start timer\n\n\nlocal function tick() -- Updates XP tally of how much experience we've gained this session\n    return UnitXP(\"player\") - xp_start + xp_carry\nend\n\n--[[\n-- Indicates the player character has leveled-up, which shifts xp values\n]]--\nfunction aura_env.level_up()\n    xp_carry = xp_carry + xp_max - xp_start\n    xp_max = UnitXPMax(\"player\")\n    xp_start = 0 -- All xp from prev lvl was added to carry, so no need to track anymore\nend\n\nlocal start_ts = time() - 1 -- Time when WA is loaded or reset (-1 to prevent div 0)\n\n--[[\n-- Updates the aura by retrieving current experience totals\n]]--\nfunction aura_env.get_data()\n    local xp_gain = tick()\n    local t = time() - ts\n    local rate = xp_gain / t\n    local xp_per_h =  math.floor(3600 * rate)\n    local ding = (xp_max - UnitXP(\"player\")) / rate\n    return xp_per_h, ding, t\nend\n\n--[[\n-- Parses an amount of seconds into a time-coded string\n--\n-- Times are rounded down to the nearest minute, second, hour, or day.\n-- 62 seconds --> \"1 min\"\n-- 2 seconds --> \"2 secs\"\n-- 3600 seconds --> \"1 hour\"\n--\n-- @param [string] secs Amount of time, in seconds\n-- @return [string] Time-coded string\n]]--\nfunction aura_env.sec_time_str(secs)\n    local num, str\n    if secs < 60 then\n        num = secs\n        str = \"sec\"\n    elseif secs < 3600 then\n        num = math.floor(secs / 60)\n        str = \"min\"\n    elseif secs < 86400 then\n        num = math.floor(secs / 3600 * 10) / 10\n        str = \"hour\"\n    else\n        num = math.floor(secs / 86400 * 10) / 10\n        str = \"day\"\n    end\n    return num .. \" \" .. str .. (num > 1 and \"s\" or \"\")\nend\n\n--[[\naura_env.sec_time_str = (function()\n        local t_scale = { 60, 60, 24, math.pow(2, 52) }\n        local t_str = { \"sec\", \"min\", \"hour\", \"day\" }\n        local t_len = #t_scale\n        return function(secs)\n            for i = 1, t_len do\n                local scale = t_scale[i]\n                if secs < scale then\n                    return secs .. \" \" .. t_str[i] .. (secs > 1 and \"s\" or \"\")\n                end\n                secs = math.floor(secs / scale)\n            end \n            return tostring(secs)\n        end\nend)()\n]]--",
