						["subeventPrefix"] = "SPELL",
						["unit"] = "player",
						["custom"] = "function(allStates, e, ...)\n    if e == \"UNIT_THREAT_LIST_UPDATE\" then\n        local unitId = ...\n        if not unitId or UnitIsDead(unitId) then return false end\n        \n        local _, _, threatPercent, _, playerThreatValue = UnitDetailedThreatSituation(\"player\", unitId)\n        if not threatPercent or threatPercent <= 0 then return false end\n        \n        local guid = UnitGUID(unitId)\n        if not guid:find('Creature') then return false end -- this event also triggers for e.g. mind controlled players\n        local npcId = select(6,strsplit('-', guid))\n        if aura_env.npcIdBlacklist[npcId] then return end\n        \n        allStates[guid] = allStates[guid] or {\n            show = true,\n            progressType = \"static\",\n            total = 100,\n            name = UnitName(unitId),\n            unit = unitId,\n            isTargetedByPlayer = guid == UnitGUID('playertarget')\n        }\n        \n        local state = allStates[guid]\n        local raidMarkIndex = GetRaidTargetIndex(unitId)\n        local tankThreat = select(5, UnitDetailedThreatSituation(unitId .. 'target', unitId)) or 0\n        local threatUntilOveraggro = 0 \n        local playerTargetGUID = UnitGUID('playertarget')\n        \n        if threatPercent > 0 then\n            threatUntilOveraggro = math.floor((playerThreatValue / (threatPercent / 100)) - playerThreatValue)\n        end\n        \n        state.changed = true\n        state.value = threatPercent\n        -- if the mob starts casting a heal on another mob, this will stop the tank's name and threat from disappearing\n        if not state.tankThreat or tankThreat > 0 then\n            state.tankThreat = tankThreat\n            state.threatUntilOveraggro = threatUntilOveraggro / 100\n            state.unitTarget = UnitName(unitId .. 'target')\n        end\n        if raidMarkIndex then\n            state.displayName = aura_env.getIconTexture(raidMarkIndex) .. state.name\n        else\n            state.displayName = state.name\n        end\n        \n        return true\n    elseif e == \"COMBAT_LOG_EVENT_UNFILTERED\" and select(8, ...) ~= aura_env.playerGUID then\n        -- remove the mob when it dies\n        local guid = select(8, ...)\n        if allStates[guid] then\n            allStates[guid].show = false\n            allStates[guid].changed = true\n            \n            return true\n        end\n    elseif e == \"PLAYER_REGEN_ENABLED\" or (e == \"COMBAT_LOG_EVENT_UNFILTERED\" and select(8, ...) == aura_env.playerGUID) then\n        -- clear all states when leaving combat or when player dies\n        for _, v in pairs(allStates) do\n            v.show = false\n            v.changed = true\n        end\n        \n        return true\n    elseif e == \"PLAYER_TARGET_CHANGED\" then\n        local targetGUID = UnitGUID(\"playertarget\")\n        \n        for k, v in pairs(allStates) do\n            if v.isTargetedByPlayer and (not targetGUID or targetGUID ~= k) then\n                v.isTargetedByPlayer = false\n                v.changed = true\n            end\n            \n            if targetGUID and k == targetGUID then\n                v.isTargetedByPlayer = true\n                v.changed = true\n            end\n        end\n        \n        return true\n    end\nend",
